for seed_index, seed in enumerate(seeds):
    np.random.seed(seed)
    data = aggregate_data_by_chunks(random_walk(n_samples=2000), chunk_size=10)
    os.makedirs(f'data/{seed_index}', exist_ok=True)
    pd.DataFrame.from_records(data[:len(data) // 2]).to_csv(f'data/{seed_index}/train.csv', index=False)
    pd.DataFrame.from_records(data[len(data) // 2:3 * len(data) // 4]).to_csv(f'data/{seed_index}/val.csv', index=False)
    pd.DataFrame.from_records(data[3 * len(data) // 4:]).to_csv(f'data/{seed_index}/test.csv', index=False)
